/*
by Dom Mandy in 2023
*/
void mainImage(out vec4 P, vec2 V) {
    vec2 R = iResolution.xy,
         C = V = 4. * (V + V - R) / R.x;

    float c, k, r = 9.,
          a = atan(C.y, C.x), 
          d = dot(C, C);

    for (; k++ < 99.; r > (c = abs(V.x)) ? r = c, R = V : R)
        c = dot(V, vec2(a, log(d) / 2.)),
        V = exp(-a * V.y) * pow(d, V.x / 2.) * vec2(cos(c), sin(c));

    P = sqrt((log(r) + 9.) / 9. * (1. + cos(atan(R.y, R.x) - vec4(0, 2, 4, 0))));
}
