//start define rotation matrix function
mat2 RotationMatrix(float degreesAngle){
float angle = degreesAngle *.01745; // convert to radians.
mat2 rot = mat2(
cos(angle), -sin(angle),
sin(angle), cos(angle)
);
return rot; }// multiply uv by this
//end define rotation matrix function

//start define reflection matrix function
mat2 ReflectionMatrix(float degreesAngle){
float angle = degreesAngle *.01745; // convert to radians.
mat2 flec = mat2(
cos(angle*2.), sin(angle*2.),
sin(angle*2.), -cos(angle*2.)
);
return flec; }// multiply uv by this
//end define reflection matrix function

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec2 uv0 = (fragCoord-.5*iResolution.xy)/iResolution.y;
vec2 uv1 = (fragCoord-.5*iResolution.xy)/iResolution.y;
vec3 col;
uv0 *= 2.;
uv1 *= 2.;


uv0*=RotationMatrix(30.+iTime);
uv1*=RotationMatrix(-30.);
uv0*=ReflectionMatrix(30.);
uv1*=ReflectionMatrix(30.);
uv0 = abs(uv0);
uv1 = abs(uv1);
vec3 channel0 = texture(iChannel0, uv0).rgb;
vec3 channel1= texture(iChannel1, uv1).rgb;
 col = channel0+channel1;
fragColor = vec4(col,1.0);

}
