void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 center = vec2(iMouse.x,iMouse.y);
    float mosaicSize = 30.0;
    float recSide = 90.0;

    vec2 uv = fragCoord / iResolution.xy;
    vec4 texColor = texture(iChannel0, uv);

    if(fragCoord.x<center.x - recSide || fragCoord.x>center.x + recSide || fragCoord.y<center.y - recSide || fragCoord.y>center.y + recSide)
    {
        fragColor = texColor;
    }
    else{
        //
        if(fragCoord.x>center.x - mosaicSize && fragCoord.x<center.x && fragCoord.y>center.y - mosaicSize && fragCoord.y<center.y){
            fragColor = texture(iChannel0, vec2(center.x-15.0,center.y-15.0)/iResolution.xy);
        }
        else if(fragCoord.x>center.x - mosaicSize && fragCoord.x<center.x && fragCoord.y<center.y + mosaicSize && fragCoord.y>center.y){
            fragColor = texture(iChannel0, vec2(center.x-15.0,center.y+15.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + mosaicSize && fragCoord.x>center.x && fragCoord.y>center.y - mosaicSize && fragCoord.y<center.y){
            fragColor = texture(iChannel0, vec2(center.x+15.0,center.y-15.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + mosaicSize && fragCoord.x>center.x && fragCoord.y<center.y + mosaicSize && fragCoord.y>center.y){
            fragColor = texture(iChannel0, vec2(center.x+15.0,center.y+15.0)/iResolution.xy);
        }

        else if(fragCoord.x>(center.x - 60.0) && fragCoord.x<center.x-30.0 && fragCoord.y>(center.y - 60.0)&& fragCoord.y<center.y-30.0){
            fragColor = texture(iChannel0, vec2(center.x-30.0,center.y-30.0)/iResolution.xy);
        }
        else if(fragCoord.x>center.x - 60.0 && fragCoord.x<center.x-30.0 && fragCoord.y<center.y + 60.0 && fragCoord.y>center.y-30.0){
            fragColor = texture(iChannel0, vec2(center.x-30.0,center.y+30.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + 60.0 && fragCoord.x>center.x+-30.0 && fragCoord.y>center.y - 60.0 && fragCoord.y<center.y-30.0){
            fragColor = texture(iChannel0, vec2(center.x+30.0,center.y-30.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + 60.0 && fragCoord.x>center.x+-30.0 && fragCoord.y<center.y + 60.0 && fragCoord.y>center.y-30.0){
            fragColor = texture(iChannel0, vec2(center.x+30.0,center.y+30.0)/iResolution.xy);
        }

        else if(fragCoord.x>(center.x - 90.0) && fragCoord.x<center.x-60.0 && fragCoord.y>(center.y - 90.0)&& fragCoord.y<center.y-60.0){
            fragColor = texture(iChannel0, vec2(center.x-60.0,center.y-60.0)/iResolution.xy);
        }
        else if(fragCoord.x>center.x - 90.0 && fragCoord.x<center.x-60.0 && fragCoord.y<center.y + 90.0 && fragCoord.y>center.y-60.0){
            fragColor = texture(iChannel0, vec2(center.x-60.0,center.y+60.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + 90.0 && fragCoord.x>center.x+-60.0 && fragCoord.y>center.y - 90.0 && fragCoord.y<center.y-60.0){
            fragColor = texture(iChannel0, vec2(center.x+60.0,center.y-60.0)/iResolution.xy);
        }
        else if(fragCoord.x<center.x + 90.0 && fragCoord.x>center.x+-60.0 && fragCoord.y<center.y + 90.0 && fragCoord.y>center.y-60.0){
            fragColor = texture(iChannel0, vec2(center.x+60.0,center.y+60.0)/iResolution.xy);
        }
    }
}
