bool circle(vec2 uvCenter, float diameter, vec2 uvCoord) {
    return distance(uvCenter * iResolution.xy, uvCoord * iResolution.xy) 
           < diameter * iResolution.y / 2.0;
}

bool rectangle(vec2 uvCenter, vec2 shape, vec2 uvCoord) {
    return abs((uvCenter.x * iResolution.x) - (uvCoord.x * iResolution.x)) 
           < (shape.x * iResolution.y / 2.0)
           &&
           abs((uvCenter.y * iResolution.y) - (uvCoord.y * iResolution.y))
           < (shape.y * iResolution.y / 2.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates
    vec2 uvCoord = fragCoord/iResolution.xy;

    // Testing

    // Circle
    //if(circle(vec2(0.5, 0.5), 0.5, uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    // Square
    //if(rectangle(vec2(0.5, 0.5), vec2(0.5, 0.5), uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    // Animation

    // Bounce horizontaly
    vec2 drawingOffset = vec2(sin(iTime * 4.0) / 10.0, 0.0);

    // Drawing
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    if(circle(vec2(0.35, 0.7) + drawingOffset, 0.25, uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    if(circle(vec2(0.65, 0.7) + drawingOffset, 0.25, uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);

    if(rectangle(vec2(0.5, 0.2) + drawingOffset, vec2(0.75, 0.1), uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    if(rectangle(vec2(0.3, 0.25) + drawingOffset, vec2(0.1, 0.2), uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    if(rectangle(vec2(0.7, 0.25) + drawingOffset, vec2(0.1, 0.2), uvCoord)) fragColor = vec4(1.0, 1.0, 1.0, 1.0);
}
