vec3 palette(float t){
    vec3 a = vec3(0.628, 0.578, -3.362);
    vec3 b = vec3(0.500, 0.500, -3.142);
    vec3 c = vec3(1.388, 1.228, 0.820);
    vec3 d = vec3(1.088, 2.158, 1.838);

    return a + b*cos(6.28318*(c+t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1). center is 0,0. fixed aspect ratio
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;


    float d = abs(length(uv.y));

    vec3 col = palette(d) * (sin(iTime) + 2.);

    d+= length(uv.x) * -abs(uv.x) * (cos(iTime * .1) + 1.);

    d = abs(d);

    d = ((cos(iTime * .5)+1.2) * .003)/d;

    col *= d;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
