void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0,(fragCoord)/iResolution.xy);
}

#define dpix iResolution.xy
#define time iTime



float get(vec2 uv,vec2 neg)
{

    return texture(iChannel0,(uv+neg/dpix)).a;

}

vec4 eva(float sum,vec4 cell)
{

    float wascell=mod(cell.a,2.);

    //康威游戏规则
    float isg=step(abs(sum-3.),0.1);
    float isl=step(abs(sum-2.),0.1);

    float iscell=step(.9,isg+isl*wascell);
    float gg=(1.-iscell)*wascell;


    //return vec4(cc,iscell);


    return vec4(iscell*cell.b,
                (1.-iscell)*cell.b,
                max(cell.b*0.98,gg),
                cell.a-wascell+iscell);


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{



    vec2 uv = fragCoord/iResolution.xy;
    //uv=floor(uv*dpix);

    vec2 touch=iMouse.xy/iResolution.xy;
    vec3 of=vec3(1.,-1.,0.);

    float sum=get(uv,of.xx)
    +get(uv,of.xy)
    +get(uv,of.xz)
    +get(uv,of.yx)
    +get(uv,of.yy)
    +get(uv,of.yz)
    +get(uv,of.zx)
    +get(uv,of.zy);

    if (iMouse.z>0.&&length(uv-touch)<.02)
        sum=3.;

    vec4 cell=texture(iChannel0,uv);

    fragColor=eva(sum,cell);

}
