#define WAVE_DETAIL 10.

float getWave( vec2 uv, float dist, float direction) {
    float speed = dist * 100.;
    float d = uv.y - .1  * sin((uv.x - 1.5 - direction) * speed + iTime) - dist + .5;
    return d;
}

vec3 getWaveColor(float dist) {
    return vec3(.4, .6, .8) * dist * 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.y;
    vec4 mouse = iMouse / iResolution.y;

    vec3 col = vec3(0.1, 0.2, .6);

    float direction = mouse.w - mouse.x;

    // sun
    col = mix(vec3(1.), col, length(uv.xy- vec2(.3 * direction + 1.5, .9)));

    float wave;
    for (float dist = .24; dist >= .01; dist -= dist / WAVE_DETAIL) {
        wave = getWave(uv, dist, direction);
        col = mix(getWaveColor(dist), col, smoothstep(.496, .504, wave));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
