void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 cPos = ( 2. * fragCoord -  iResolution.xy ) / iResolution.y;
    float cLength = length(cPos);

    vec2 uv = fragCoord.xy / iResolution.xy + (cPos / cLength) * cos(cLength * 12.0 - iTime * 4.0);
    float strength = smoothstep(100./iResolution.y, 0., distance(uv, vec2(0.5)));

    fragColor = vec4(vec3(strength), 1.0);
}
