// golfing 480 chars https://shadertoy.com/view/4llBRl
// ( see original for comments )

void mainImage( out vec4 O, vec2 u )
{
	vec2  R = iResolution.xy,  
          M = length(iMouse.xy) > 10. ? iMouse.xy / R : vec2(.55,.7);
    float d = 2./R.y, x, l,v;                          

    for( O *= x = 0. ; x < 5.; x += d ) 
        l = length( ( u+u - R ) / R.y  - vec2( x-1.2, .2* sin(x+x-iTime) ) ), 
        v = ( l - 2.*M.x ) / exp2(7.+5.*M.y),
        O -=  cos( 27.8 * x / sqrt(l) ) / l  / exp(v*v*3e6) *d *.34;

    O += .5;
}
