vec3 h3(vec3 p3) 
{
	p3 = fract(p3 * vec3(0.1031, 0.1030, 0.0973));
    p3 += dot(p3, p3.yxz + 33.33);
    return fract((p3.xxy + p3.yxx) * p3.zyx);
}

vec2 h2(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(0.1031, 0.1030, 0.0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

void mainImage(out vec4 f, in vec2 c)
{
    vec2 u = c / iResolution.xy;
    u = (u - 0.5) * 1.1 + 0.5;
    float s = 15.0;
    float t = floor(iTime * s);

    vec2 l = iResolution.xy / h2(t) / 300.0;
    vec2 p = floor(u * l);
    vec3 r = h3(vec3(p, t));

    vec2 o = vec2(cos(r.x * 6.283), sin(r.x * 3.283)) * r.y;
    float d = sin(fract(iTime * s) * 0.94);
    vec2 sc = 50.0 / iResolution.xy;
    float th = step(0.7, r.z);
    u += o * th * d * sc;

    vec2 rgb = 10.0 / iResolution.xy * d * th * 10.0;
    f.r = texture(iChannel0, u + rgb).r;
    f.g = texture(iChannel0, u).g;
    f.b = texture(iChannel0, u - rgb).b;
    f.a = 1.0;

    f.rgb *= step(0.0, u.x) * step(u.x, 1.0) * step(0.0, u.y) * step(u.y, 1.0);
}
