#define s(x) 1.-smoothstep(0.005, 0.015, x)
void mainImage( out vec4 o, in vec2 f )
{
    vec2 uv = (f.xy / iResolution.xy * 2. -1.)*2.;
    uv.x*=iResolution.x/iResolution.y;
    vec2 d = abs(fract(uv) - .5);
    float d2 = s(length(d)/4.);
    o.rgb = vec3(.5*length(s(d/3.))+d2*3.);
}
