// Fork of "ping1" by makscee. https://shadertoy.com/view/DtscRH

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xx;
    float offset = texture(iChannel0, uv * 200.5 + vec2(iTime * 0.5 + sin(iTime) * .2, 0)).x;
    uv *= 15.;
    uv += vec2(offset);

    vec2 central = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float r = length(central) / 2.;

    vec3 col = vec3(
        r < sin(iTime / 1.5),  
        float(length(uv - round(uv))) > 0.3,
        r > cos(iTime / 1.5));

    fragColor = vec4(col,1.0);
}
