void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the aspect ratio
    float aspectRatio = iResolution.y / iResolution.x;

	// Calculate the normalized pixel coordinates
    vec2 uv = ( fragCoord.xy / iResolution.xy ) * vec2( 1.0, aspectRatio );
    float position = 0.0;

    // Time varying blob position and size
    position = distance( uv, vec2( 0.4, 0.4 * aspectRatio ) ) 
        * distance( uv, vec2( 0.1 * aspectRatio + abs( sin( iTime / 1.3 ) ) ) );

    float circ = step( 0.1, position );

    vec3 color = vec3( 0.8, circ, circ );

    // Output to screen
	fragColor = vec4( color, 1.0 );
}
