// Fork of "Chladni Diagram" by CeruleanBoolean141. https://shadertoy.com/view/dttGz2
// 2023-05-01 22:45:00

//Created by CeruleanBoolean141 April 2023
//Thanks to user FabriceNeyret2 for antialiasing method and other advice

#define SCALE   5.0
#define ESCALE 3.0 //8.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
     vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;
    vec2 mouse2 = vec2(1.0);

    if( texture(iChannel0, vec2( 69./256.0, 2./2.0 ) ).x > 0. ) {
       mouse2 = mouse;
       mouse = vec2(1.);
    }

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float pi = 3.14159, t = 0.5*iTime;
    vec2 nm = vec2(1.0*(mouse.x*SCALE),4.0*(mouse.y*SCALE)); //higher values -> shorter periods.  ratio effects overall shape

    vec2 ab = vec2(sin(t)*1.2*(mouse2.x*ESCALE),cos(t)*0.49*(mouse2.y*ESCALE)); //larger values increase "sharpness", effects shape
    float col =  abs(ab.x*sin(pi*uv.x*nm.y)*sin(pi*uv.y*nm.x) + ab.y*sin(pi*uv.x*nm.x)*sin(pi*uv.y*nm.y));   
    fragColor = mix(vec4(0.8), vec4(col*vec3(sin(t),uv),1.0), smoothstep(0., 10./iResolution.y ,col-.1));

}
