float calc(in vec2 fragCoord, in vec2 pos){
    vec2 uvx1 = (fragCoord + vec2(1, 0) + pos)/iResolution.xy;
    vec2 uvx2 = (fragCoord + vec2(-1, 0) + pos)/iResolution.xy;
    vec2 uvy1 = (fragCoord + vec2(0, 1) + pos)/iResolution.xy;
    vec2 uvy2 = (fragCoord + vec2(0, -1) + pos)/iResolution.xy;

    vec4 texColx1 = texture(iChannel0, uvx1);
    vec4 texColx2 = texture(iChannel0, uvx2);
    vec4 texColy1 = texture(iChannel0, uvy1);
    vec4 texColy2 = texture(iChannel0, uvy2);

    float xVal1 = (texColx1.x + texColx1.y + texColx1.z) / 3.0;
    float xVal2 = (texColx2.x + texColx2.y + texColx2.z) / 3.0;
    float yVal1 = (texColy1.x + texColy1.y + texColy1.z) / 3.0;
    float yVal2 = (texColy2.x + texColy2.y + texColy2.z) / 3.0;

    float dx = abs(xVal1 - xVal2);
    float dy = abs(yVal1 - yVal2);
    return sqrt(pow(dx, 2.) + pow(dy, 2.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float grx1 = calc(fragCoord, vec2(-1, 0));
    float grx2 = calc(fragCoord, vec2(1, 0));
    float gry1 = calc(fragCoord, vec2(0, -1));
    float gry2 = calc(fragCoord, vec2(0, 1));

    float dx2 = abs(grx1 - grx2);
    float dy2 = abs(gry1 - gry2);
    float lp = sqrt(pow(dx2, 2.) + pow(dy2, 2.));

    fragColor = vec4(vec3(lp), 1.0);    
}
