// variant of https://shadertoy.com/view/DdGGDz
// variant of "Simple fire effect" by guil. https://shadertoy.com/view/msyGRm

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy ,
          p = 4.*( u+u - R ) / R.y,
          r = sin( p+iTime*2.5 );

    r += sin( p * mat2(8,6,-8,6)*.16 + r*.64 +iTime*2.5 ) / 1.6,
    O = vec4(.5+ .5*sin(.2*dFdx(length(r))*R.y) );
}
