#define A 0.05          // Animation speed factor
#define B 30            // Fractal iterations
#define C 9             // Fractal sequence length
#define D 20            // Array size for weights
#define E 0.97          // Weight base value
#define F 0.028         // Weight oscillation amplitude
#define G 4.001         // Weight phase offset
#define H 0.76          // Weight oscillation speed
#define I 2.0           // X update factor
#define J 3.14159265    // Pi constant
#define K 0.8           // Color shift red
#define L 0.55          // Color shift green
#define M 0.00330       // Color shift blue
#define N 0.74          // Color shift speed
#define O 0.5           // Color component base
#define P 0.3           // Color2 red offset
#define Q 0.33          // Color2 green offset

precision highp float;
#define ANIMATION_SPEED_FACTOR A
#define FRACTAL_ITERATIONS B
#define FRACTAL_SEQUENCE_LENGTH C
int fractalSequence[] = int[C](0, 0, 1, 0, 0, 0, 1, 1, 1);
vec3 computeColor(vec2 pixelCoordinate) {
    float currentTime = iTime * A;
    float weights[D];
    float x = 0.5;
    float height = 0.0;
    for (int i = 0; i < D; i++) {
        weights[i] = E + F * sin(float(i) + G + H * currentTime);
    }
    for (int iteration = 0; iteration < B; iteration++) {
        for (int step = 0; step < C; ++step) {
            x = weights[step] * pixelCoordinate[fractalSequence[step]] * x * (1.0 - x);
            height += log2(abs(weights[step] * pixelCoordinate[fractalSequence[step]] * (1.0 - I * x)));
        }
    }
    height /= float(B) * float(C);
    vec3 colorShift = vec3(K, L, M) + currentTime * N;
    vec3 color1 = cos((colorShift + 2.5 * abs(height)) * J) * O + O;
    vec3 color2 = vec3(O) + O * sin(vec3(P, Q, P) + 2.5 * abs(height) + currentTime * O);
    return (height < 0.0) ? mix(color1, color2, pow(abs(height), .625)) : vec3(0.0);
}
void mainImage(out vec4 finalColor, in vec2 pixelCoordinate) {
    vec2 centeredCoordinate = pixelCoordinate - iResolution.xy * 0.5; // Move the fractal center to the screen center
    finalColor = vec4(computeColor(3.0 + (1.0 * centeredCoordinate.xy / iResolution.xy)), 1.0);
}
