float myMod(float v1, float v2) {
    return ((v1 / v2) - floor(v1 / v2));
}

float lerp(float a, float b, float t){
    return a + (b-a) * t;
}

vec3 palette(in float t)
{
    vec3 a = vec3(0.500, 0.500, 0.500);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(1.000, 1.000, 1.000);
    vec3 d = vec3(0.263, 0.416, 0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 center = vec2(cos(iTime), sin(iTime)) * 0.5;
    vec2 offset = uv - center;
    float st = 1.0 + sin(iTime);
    float a = max(abs(offset.x), abs(offset.y));
    float b = smoothstep(0.0, 1.0, length(offset));
    float v1 = lerp(a, b, st / 2.0);
    //float thresh = 0.25 * st;
    float thresh = 0.25;
    float lum = 1.0 - smoothstep(thresh, thresh + 0.1, v1);
    vec3 col = palette(lum + myMod(iTime, 1.0));
    if (lum == 0.0 || lum == 1.0) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(col,1.0);
    }
}
