float noise (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(22.9898,78.233)))*
        234758.5453123);
}

float perlin(vec2 st) {
    vec2 scale =  st;
    vec2 id = floor(scale);
    vec2 guv = fract(scale);
    guv = smoothstep(0., 1., guv);


    float noiseTL = noise(id);
    float noiseTR = noise(id + vec2(1., 0.));
    float noiseT = mix(noiseTL, noiseTR, guv.x * guv.x);

    float noiseBL = noise(id + vec2(0., 1));
    float noiseBR = noise(id + vec2(1., 1.));
    float noiseB = mix(noiseBL, noiseBR, guv.x * guv.x);

    float noiseValue = mix(noiseT, noiseB, guv.y * guv.y);

    return noiseValue;
}


float fbm (vec2 st) {

    float amp = 1.;
    float freq = 1.;


    float color = 0.;
    for(float i = 0.; i < 10.; i++) {
        color += amp * perlin(st * freq + perlin(st + i * iTime));
        freq *= 2.;
        amp *= 0.5;

    }

    return color/2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy) / iResolution.y;



    // Time varying pixel color
    vec3 col = vec3(0.);
    col += 1.;

    vec2 oUv = uv;
    uv.x = mix(abs(uv.x) * 3., sqrt(abs(uv.x)), smoothstep(-0.4, 0.4, uv.y));
    float d = length(uv);
    float t = d - 0.1 * fbm(uv + iTime);
    col *= smoothstep(0.25, 0.19, t) - 0.1 * smoothstep(0.2, 0.01, t) * (1. - uv.y);

    col *= mix(vec3(3.,1.3,1.), vec3(5., 3., 1.), col);
    col = mix(vec3(fbm(oUv * 100.)) * 0.1 + vec3(0.8, 0.8, 0.8), col, smoothstep(0.5, 1., length(col)));
    // Output to screen
    fragColor = vec4(col,1.0);
}
