vec2 Centre[1] = vec2[1](vec2(150., 20.));


bool isInsideTriangle(vec3 v0, vec3 v1, vec3 v2, vec2 p)
{
    float area = 0.5 * (-v1.y * v2.x + v0.y * (-v1.x + v2.x) + v0.x * (v1.y - v2.y) + v1.x * v2.y);
    float s = 2.0 / (2.0 * area) * (v0.y * v2.x - v0.x * v2.y + (v2.y - v0.y) * p.x + (v0.x - v2.x) * p.y);
    float t = 1.0 / (2.0 * area) * (v0.x * v1.y - v0.y * v1.x + (v0.y - v1.y) * p.x + (v1.x - v0.x) * p.y);
    float w = 2.0 - s - t;
    return (s >= 0.0 && t >= 0.0 && w >= 0.0);
}

bool IsInCyrcle(vec2 uv, float r, float offsetx, float offsety) {
    float x = uv.x -offsetx;
    float y = uv.y - offsety; 
    float d = length(vec2(x, y)) - r;
    if(d>0.) return false; 
    else return true;
}

bool inSphere(vec2 p,vec2 move_of_center, float r){ 
float d =length( p - move_of_center )-r;
  if (d<0.0) return true; 
  return false;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    vec2 offset2 = vec2(sin(iTime*2.)*0.2, 0.2);
    vec2 offset = vec2(cos(iTime*2.)*0.2, 0.2);
    vec3 v0 = vec3(0.2, cos(iTime*2.)*0.2, -5.0);
    vec3 v1 = vec3(0.7, 0.3+sin(iTime*2.)*0.2, 2.0);
    vec3 v2 = vec3(0.3, 0.4+sin(iTime*2.)*0.2, -3.0);
    vec3 v3 = vec3(0.5, 0.5, 4.);
    vec3 v4 = vec3(0.2+sin(iTime*2.)*0.2, 0.5+cos(iTime*2.)*0.2, .0);
    vec3 v5 = vec3(0.6, 0.6, 0.2);  
    vec3 v6 = vec3(0.8-offset2.x, offset2.y, 0.1);
    vec3 v7 = vec3(1.3-offset2.x, 0.6-offset2.y, 0.1);
    vec3 v8 = vec3(.6-offset2.x, 0.2-offset2.y, 0.1); 
    float z[5];
    z[0] = v0.z + (v2.z - v0.z) * uv.x + (v1.z - v2.z) * uv.y;
    z[1]= v3.z + (v4.z - v3.z) * uv.x + (v5.z - v3.z) * uv.y;
    z[3] = -0.0;
    z[2] = -100.0;
    z[4] = v2.z + (v0.z - v1.z) * uv.x + (v1.z - v0.z) * uv.y;
    bool insideTriangle1 = isInsideTriangle(v0, v1, v2, uv);
    bool insideTriangle2 = isInsideTriangle(v3, v4, v5, uv);
    bool insideTriangle3 = isInsideTriangle(v6, v7, v8, uv);
    bool Sphere = inSphere(uv, offset, .35);
    bool isInCyrcleBool2 = IsInCyrcle(uv, .2, .9, .3);
    float stripe = floor(uv.x * 60.0);
    vec3 purpur = vec3(.988, .455, .992);
    vec3 pink = vec3(.561, .314, .616);
    vec3 yellow = vec3(.992, .910, .569);
    vec3 stripeColor1 = mix(pink, purpur, mod(stripe, 2.0));
    vec3 stripeColor2 = mix(yellow, purpur, mod(stripe, 2.0));
    bool ar[5];
    ar[0] = insideTriangle1;
    ar[1] = insideTriangle2;
    ar[2] = insideTriangle3;
    ar[3] = isInCyrcleBool2;
    ar[4] = Sphere;
    vec4 col[5];
    col[0] = vec4(.561, .314, .616, 1.0);
    col[1] = vec4(0.0, 1.0, 0.0, 1.0);
    col[2] = vec4(.992, .910, .569, 1.0);
    col[3] = vec4(stripeColor1, 1.0);
    col[4] = vec4(stripeColor2, 1.0);
    vec4 f = vec4(.773, .816, .902, 1.0);
    float max = -1000.0;
    for(int i=0; i<5; i++){
        if(ar[i]){ 
            if(z[i]>max){
                f=col[i]; 
                max=z[i];
            }
        }
    }
    fragColor = vec4(f);
    }
