vec3 Circle(vec2 uv, float r, vec3 color) {
  float x = uv.x;
  float y = uv.y;

  float d = length(vec2(x, y)) - r;

  return d > 0.0 ? vec3(1.0) : color;
}

vec3 Ring(vec2 uv, float r, vec3 color) {
  float x = uv.x;
  float y = uv.y;

  float d = length(vec2(x, y)) - r;
  d = abs(d);
  d = smoothstep(0.025, 0.05, d);

  return vec3(mix(color, vec3(1.0), d));
}

float Segment(in vec2 p, in vec2 a, in vec2 b) {
  vec2 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  return smoothstep(0.005, 0.015, length(pa - ba * h));
}

float Segment2(in vec2 p, in vec2 a, in vec2 b) {
  vec2 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  return smoothstep(0.015, 0.025, length(pa - ba * h));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // Time varying pixel color
    vec3 col;
    vec3 mins;
    vec3 sec;
    vec3 hrs;

    //col = Circle(uv, 0.9, vec3(0.0));
    //col = max(-Circle(uv, 0.75, vec3(1.0)), col);

    col = Ring(uv, 0.9, vec3(0.0));
    mins = vec3(Segment2(uv, vec2(0.0), vec2(0.5)));
    hrs = vec3(Segment2(uv, vec2(0.0), vec2(-0.5, 0.25)));
    sec = vec3(Segment(uv, vec2(0.0), vec2(0.0, 0.6)));
    sec.r += 1.0;
    col = min(col, Circle(uv, 0.05, vec3(0.0)));

    vec3 time;
    vec3 hands;
    vec3 final;

    time = min(hrs, mins);
    hands = min(time, sec);
    final = min(hands, col);

    // Output to screen
    fragColor = vec4(final, 1.0);
}
