vec3 steam_color = vec3(1.0);
float time_shift = 0.5;
float cloudSize1 = 100.0;
float cloudSize2 = 0.5;
float brightness = 0.3;

float flip(float t)
{
    return 1.0 - t;
}

float lerp(float a, float b, float t)
{
    return a + (b - a) * t;
}

float expStep( float x, float k, float n)
{
    return exp( -k*pow(x,n) );
}

vec3 Noise(vec2 uv)
{
    vec4 noise = texture(iChannel0, vec2(uv.x - iTime * time_shift, uv.y));
    noise = vec4(round(noise.rrr * lerp(cloudSize1, cloudSize2, uv.x))* brightness, 0.3); //makes texture into two colors
    return noise.xxx;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(vec3(Noise(uv)), 1.0);
}
