#define P(a) p -= 2.*max(dot(a,p),0.)*a

void mainImage( out vec4 O, vec2 F )
{
    vec2  R = iResolution.xy,
          u = ( F+F - R ) / R.y,
          a = vec2(.81, -.59),
          c = vec2(-a.x,a.y),
          b = -c.yx -vec2(0,1), p;
    float l = length(u), t = iTime, B, i,d;

    for( O *= i ; i++ < 3.;
         O =  pow( .02 / abs( sin( 3. * ( length(u) + B/exp(l) ) + t*.2 )), 1.2 )
             *( 1.+ cos(6.*( l + (i+t)*.4 + vec4(6.5,.5,6.9,0) )))   
       )
        p = u = fract( u *= 1.5* mat2(cos(t*.058 -vec4(0,11,33,0))) ) - .5,
        p.x = abs(p.x),
        P(a), P(c),
        p.x = abs(p.x),
        p.y -= 2.,
        d = dot(p,b),
        B =   length( p - b* clamp( d/dot(b,b), 0., 2. ))
            * sign(p.x-d);      
}
