float wave(in vec2 uv) {
    float vf=15.0*(sin(iTime)+1.0)/2.0;
    return 0.3*((sin(vf * (uv.x+uv.y) )+1.0)/2.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy - vec2(1.0,1.0));

    // Output to screen
    uv *=2.0;
    uv -= 1.0;

    uv.y *= iResolution.y/iResolution.x;

    fragColor.rgb=vec3(0.0,0.0,0.0)+wave(uv);

    if( uv.y < 0.21) {
        fragColor.rgb=vec3(1.0,1.0,1.0)+wave(uv);
    }

    if( uv.y < -0.21 ) {
        fragColor.rgb=vec3(0.0,0.7+wave(uv),0.1);
    }

    if (uv.x+0.44<uv.y && uv.y<0.0) {
        fragColor.rgb=vec3(0.7+wave(uv),0.0,0.1);
    }

    if (uv.x+0.44<-uv.y && uv.y>=0.0) {
        fragColor.rgb=vec3(0.7+wave(uv),0.0,0.1);
    }
}
