vec3 blue = vec3(0.4,0.8,1.0);
float zoom = 0.0;
vec2 offset = vec2(0.0);

float square(float base){
    return(pow(base,2.0));
}

vec3 mandelbrot(vec2 fragCoord, float scale){

    float threshHold = 10.0;
    float iterationNum = 1000.0;

    vec2 c = fragCoord/scale - offset;
    //c = c/scale;
    vec2 z = vec2(0.0);
    float zSq= 0.0;

    for (float i = 0.0; i < iterationNum ; i++){

        z =  vec2(square(z.x)-square(z.y) ,2.0*(z.x)*(z.y))+ c;
        zSq= square(z.x)+square(z.y);

        if (zSq > threshHold){

            return(blue*( 1.0-(100.0-i)/100.0));
        }
    }


    return(vec3(0.9)+0.2*blue);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // zoom
    if(1 > 0){
        offset = vec2(0.169,-0.192)*iResolution.xy/(iResolution.x/4.0) ;
        zoom = iTime;
    }
    //no zoom
    else{
        offset = vec2(0.5,0.5)*iResolution.xy/(iResolution.x/4.0) ;
    }

    vec3 col = mandelbrot(fragCoord, iResolution.x/4.0 + 500.0*zoom);

    // Output to screen
    fragColor = vec4(col,1.0);
}
