// Smooth HSV2RGB from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(in vec3 c)
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 color = vec4(0.5, 0.5, 0.5, 1.0);
    // Loop over all boids
    vec4 bcolor = vec4(0.0, 0.0, 0.0, 1.0);
    bool hit = false;
    for (int i = 0; i < NUM_BOIDS; i++) {
        ivec2 tex_pos = ivec2(i % int(iChannelResolution[0].x),
                                                i / int(iChannelResolution[0].x));
        vec4 pos_vel = texelFetch(iChannel0, tex_pos, 0);
        if (distance(fragCoord, pos_vel.xy) < BOIDS_SIZE) {
            vec3 rgb = hsv2rgb(vec3((float(i)+0.5) / float(NUM_BOIDS), 1.0, 0.9));
            bcolor += vec4(rgb, 1.0);
            hit = true;
        }
    }
    if (hit) {
        color = bcolor;
    }

    // Output to screen
    fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if (iFrame == 0) {
        // initialize the buffer
        vec4 pos_vel = texture(iChannel1, fragCoord/iChannelResolution[1].xy);
        pos_vel.xy = iResolution.xy / 2.0 + (pos_vel.xy - 0.5) * iResolution.xy * 0.2;
        pos_vel.zw = (pos_vel.zw - 0.5) * iResolution.xy * 1.0;
        fragColor = pos_vel;
        return;
    }
    ivec2 iFragCoord = ivec2(fragCoord);
    // 
    vec4 pos_vel = texelFetch(iChannel0, iFragCoord, 0);
    vec2 pos = pos_vel.xy;
    vec2 vel = pos_vel.zw;
    // ID of the current Boid
    int my_id = int(iFragCoord.y) * int(iResolution.x) + int(iFragCoord.x);

    // Implement Boids here
    float s_min = 5.0;
    float s_max = 10.0;
    float d_s = 30.0;
    float d_v = 150.0;
    float d_left = 200.0;
    float d_right = iResolution.x - 200.0;
    float d_top = 160.0;
    float d_bottom = iResolution.y - 160.0;
    float alpha_s = 0.02;
    float alpha_c = 0.001;
    float alpha_a = 0.03;
    float alpha_b = 0.5;

    vec2 dist_sum = vec2(0.0, 0.0);
    float n_s = 0.0;

    vec2 pos_sum = vec2(0.0, 0.0);
    float n_v = 0.0;

    vec2 vel_sum = vec2(0.0, 0.0);

    for (int i = 0; i < NUM_BOIDS; i++) {
        ivec2 tex_pos_i = ivec2(i % int(iChannelResolution[0].x),
                                i / int(iChannelResolution[0].x));
        vec4 pos_vel_i = texelFetch(iChannel0, tex_pos_i, 0);

        vec2 pos_i = pos_vel_i.xy;
        vec2 vel_i = pos_vel_i.zw;

        int i_id = int(pos_i.y) * int(iResolution.x) + int(pos_i.x);
        if (i_id == my_id) continue;

        if (distance(pos, pos_i) < d_s) {
            dist_sum += pos - pos_i;
            n_s++;
        }

        if (distance(pos, pos_i) < d_v) {
            pos_sum += pos_i;
            vel_sum += vel_i;
            n_v++;
        }
    }

    vec2 avg_pos = pos_sum / n_v;
    vec2 avg_vel = vel_sum / n_v;

    float fbl = pos.x < d_left ? 1.0 : 0.0;
    float fbr = pos.x > d_right ? 1.0 : 0.0;
    float fbt = pos.y < d_top ? 1.0 : 0.0;
    float fbb = pos.y > d_bottom ? 1.0 : 0.0;

    vec2 f_s = dist_sum / n_s;
    vec2 f_c = avg_pos - pos;
    vec2 f_a = avg_vel - vel;
    vec2 f_b = vec2(fbl - fbr, fbt - fbb);

    vec2 f = alpha_s * f_s + alpha_c * f_c + alpha_a * f_a + alpha_b * f_b;

    vel += f / 1.0;
    float speed = length(vel);

    if (speed > 0.0) {
        if (speed > s_max) {
            vel = vel * (s_max / speed);
        }
        if (speed < s_min) {
            vel = vel * (s_min / speed);
        }
    }

    pos += vel;

    fragColor = vec4(pos, vel);
}
