renderpass.code:
const float speed = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //main
	vec2 p_m = fragCoord.xy / iResolution.xy;

    //distortion
    vec2 p_d = p_m;

    //move the distortion
    p_d.t += iTime * speed;

    //dst = distortion in this case
    vec4 dst_map_val = texture(iChannel0, p_d);

    //from 0 to 1  -> -1 to 1
    vec2 dst_offset = dst_map_val.xy;
    dst_offset -= vec2(0.0, 0.0);
    dst_offset *= 2.;

    dst_offset *= 0.01;

    vec2 dist_tex_coord = p_m.st + dst_offset;
    fragColor = texture(iChannel0, dist_tex_coord); 
}

