#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

float rectSDF(vec2 uv, vec2 s) {
    return max(abs(uv.x / s.x), abs(uv.y / s.y));
}
float fill(float x) {
    return step(x, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    vec2 M = iMouse.xy / iResolution.xy;

    float fx = fract(uv.x * 12.0);

    float vRect = fill(rectSDF(vec2(fx, uv.y), vec2(0.8, 1.0)));
    float hRect = fill(rectSDF(uv - vec2(0.0, M.y), vec2(2.0, 0.05)));

    vec3 color = vec3(1.0);
    float fix = floor(fx);

    // hue from https://www.shadertoy.com/view/ll2cDc
    vec3 vRectColor = cos( 3.0 * uv.x + vec3(0, 23, 21)) - 0.02;
    vec3 hRectColor = vec3(1.0, 0.855, 0.0);

    color = vRect * vRectColor;
    color = hRect == 1.0 ? hRectColor : color;
    color = hRect * vRect == 1.0 ? vRectColor : color;
    color = mix(color, hRectColor, hRect * M.y * 1.1);

    // Output to screen
    fragColor = vec4(color,1.0);
}
