// textureless randomization of bufferless simplification of "Dust [415 Chars]" by Xor. https://shadertoy.com/view/cdG3Wd

#define R mat2(cos(vec4(0,11,33,0)
#define K uint

void mainImage(out vec4 O, vec2 I) {
    vec3  r = iResolution,
          d = vec3( I+I, r ) - r,
          p = iTime/r/.3 - 6., c = p, v, s;
    float i, n = 1e2, l = -n;
    K q =
      K(iFrame) * 37769685u
      ^ K(I.x) * 26757677u
      ^ K(I.y) * 20501397u;

    for( d.yz *= R-.3)); l++ < n; )
      for( s = v = p += d/length(d) *s.y, i = n ; i>.1 ; i*=.4 )
        v.xz *= R+2.)),
        s = max( s, min( v = i*.8-abs(mod(v,i+i)-i), min(v.x, v.z)) ),
        l == 0.
          ? p += (c-p) / exp(float(q*=594156893u)*2.5e-8),
            d /= d,
            O *= l
          : O += s.y;  


}
