#define CELL_SIZE 9.
// 0 : Inv back
// 1 : Value
// 2 : Src color
#define VERSION 2
#define PI 3.14159265359
#define BACK vec3( 0. )
#define ROTATION 45.

vec2 rotate( in vec2 uv, in float deg )
{
    float rad = deg * PI / 180.;
    return vec2( uv.x * cos(rad) - uv.y * sin(rad),
            uv.x * sin(rad) + uv.y * cos(rad) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = rotate( fragCoord/CELL_SIZE + vec2(.5), -ROTATION);
    vec2 id = floor( uv );

    uv = fract( uv );
    uv -= .5;
    vec4 src = texture(iChannel0, rotate( (id*CELL_SIZE+uv), ROTATION )/iResolution.xy);
    float d = length( uv );
    float value = max( src.r, max( src.g, src.b ) );
    vec3 c;
    if ( /*Inv back*/ VERSION == 0 )
    {
        c = vec3(1.) - BACK;
    }
    else if ( /*Value*/ VERSION == 1 )
    {
        c = vec3(value);
    }
    else if ( /*Src color*/ VERSION == 2 )
    {
        c = src.rgb;
    }
    vec3 col = mix( c, BACK, smoothstep( -.13, .0, d-value*.5));
    fragColor = vec4(col,1.0);
}
