vec3 invertColor(vec3 col){
    vec3 outColor = vec3(1.0, 1.0, 1.0) - col;

    return outColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(invertColor(vec3(uv, 0.0)), 1.0);
}
