#define t iTime
#define r iResolution.xy

vec3 palette(in float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(.32,.18,1.);
    return a + b*cos( 6.28318*(c*t+d) );
}
vec2 rotate(vec2 p,float a){
	float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<3;i++) {
		vec2 uv0,p=fragCoord.xy/r;
		uv0=p;
        uv0-=.5;
		p-=.5;
		p.x*=r.x/r.y;
		z+=.07;
		l=length(p);
		uv0+=p/l*(sin(z)+1.)*abs(sin(l*9.-z-z));

        uv0 = rotate(uv0,t*.1+length(uv0)+sin(length(uv0)*2.));

		c[i]=.01/length(mod(uv0,1.)-.5);
        c[i] /= palette(.01/length(mod(uv0,1.)-.5))[i];
	}
	fragColor=vec4(c/l,1.);
}
