//Based on https://www.shadertoy.com/view/Wt33Wf by kaiware007

float sun(vec2 uv, float battery)
{
    float val = smoothstep(0.3, 0.29, length(uv));
    float bloom = smoothstep(0.7, 0.0, length(uv));
    float cut = 3.0 * sin((uv.y + iTime * 0.1 * (battery + 0.002)) * 80.0) 
                + clamp(uv.y * 15.0 + 1.0, -6.0, 6.0);
    cut = clamp(cut, 0.0, 0.8);
    return clamp(val * cut, 0.1, 1.0) + bloom * 0.4;
}

float grid(vec2 uv, float battery)
{
    vec2 size = vec2(uv.y, uv.y * uv.y * 0.5) * 0.01;
    uv += vec2(0.0, iTime * 4.0 * (battery + 0.05));
    uv = abs(fract(uv) - 0.5);
    vec2 lines = smoothstep(size, vec2(0.0), uv);
    lines += smoothstep(size * 5.0, vec2(0.0), uv) * 0.4 * battery;
    return clamp(lines.x + lines.y, 0.0, 3.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    float battery = 0.5; // Assume full battery for simplicity

    // Grid
    // float fog = smoothstep(0.1, -0.02, abs(uv.y + 0.2));
    vec3 col = vec3(1.0, 0.0, 0.0);
    if (uv.y < -0.19)
    {
        uv.y = 1.0 / (abs(uv.y + 0.2) + 0.05);
        uv.x *= uv.y * 1.0;
        float gridVal = grid(uv, battery);
        col = mix(col, vec3(1.0, 1.0, 1.0), gridVal);
    }

    // Sun calculation
    vec2 sunPosition = vec2(0.0, 0.3); // Center the sun in the viewport
    float sunVal = sun(uv - sunPosition, battery);

    col = mix(vec3(1.0, 1.0, 1.0), col, sunVal);

    fragColor = vec4(col, 1.0);
}
