#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
/* oroza  original https://www.shadertoy.com/view/dl3XDS */
mat2 rotationMatrix(float angle)
{
angle *= 3.14 / 180.0;
    float s=sin(angle), c=cos(angle);
    return mat2( c, s, -s, c );
}
vec2 rotate(vec2 v, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return vec2(v.x * c - v.y * s, v.x * s + v.y * c);
}
void mainImage(out vec4 O, vec2 C)
{


    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
     float t = iTime * .1 + ((.25 + .05 * sin(iTime * 1.1))/(length(d.xy) + .022)) * 1.2;
float si = sin(t);
float co = cos(t);
mat2 ma = mat2(co, si, -si, co);
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p=g*d;
        p.z+=-iTime*2.25;
          p.xy*=rotationMatrix(10.*iTime);
        ;
p.xy*=ma;

        a=13.;
        p=mod(p-a,a*2.)-a;
        s=3.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
              p.xy*=-rotationMatrix(1.*iTime);
            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e-
                vec3(
                    5.+cos(iTime*.3+.5*fract(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.5)*5.
                 );
         }
         g+=e=length(p.yz)/s;
    }
}
