void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    float t = iTime * .5;

    if (iMouse.xy==vec2(0)) {
        float a = pow(t, .5) * .1;
        vec2 b = vec2(5, .5);
        mouse = a * vec2(cos(t), sin(t)) + b;
    }

    vec3 col = vec3(0.);

    float phi = mouse.x * .25 * 3.14159265;
    vec2 n = vec2(sin(phi), cos(phi));

    float scale = 1.;
    uv *= .6;
    scale *= .6; 

    for (int i = 0; i < 8; i ++) {
        float s = (mouse.y + .5) * 1.5;
        scale *= s;
        uv *= s;

        uv.xy = abs(uv.xy);
        uv.xy -= .5;
        uv -= n*min(0., dot(uv, n))*2.;
    }

    float d = length(uv - vec2(clamp(uv.x, 0., 10.), 0.));
    col += smoothstep(1./iResolution.y, .0, .05*d/scale);
    col.rg += uv;

    fragColor = vec4(col,1.0);
}
