vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;
    float y = -1.0 + 2.0 * (fragCoord.y / iResolution.y);
    float x = (-1.0 + 2.0 * (fragCoord.x / iResolution.x)) * ratio;
    float time = 0.2 * iTime;
    vec2 uv = vec2(x, y);
    vec2 origin = vec2(0.0, 0.0);
    float dist = distance(uv, origin);

    float wave = 0.5 + 0.5 * sin(time + 10.0*(-time + dist));
    float stepped = step(0.5, wave);
    float colorTime = dist - 1.0 * time;
    vec3 col = pal( colorTime, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.10,0.20) );
    fragColor = vec4(stepped * col, 0.5);
    // fragColor = vec4(stepped, stepped, stepped, 0.5);
}
