#define HPI 1.57079632679
#define PI 3.14159265359
#define TAU 6.28318530718

vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec4 stochasticBokehBlur(sampler2D tex, vec2 p, float radius, float ratio, float gamma, float samples)
{
    vec4 acc = vec4(0);
    for(float i = 0.; i < samples; i++)
    {
        vec2 rnd = hash22(p+i+floor(iTime/0.05))*vec2(1, 1);
        rnd.y*=rnd.y;
        rnd = vec2(cos(rnd.x), sin(rnd.x))*rnd.y*radius;
        rnd.x *= ratio;
        acc += pow(texture(tex, p+rnd), vec4(gamma));
    }
    return pow(acc/samples, 1./vec4(gamma));
}

