#define rot(a)    mat2(cos(a+vec4(0,11,33,0)))                  // rotation
#define H(P)      fract(sin(ceil(P.x)*71.3))                    // random 1D
//#define H(P)    fract(sin(ceil(P)*mat2(R,71)))                // random 2D

#define T texture(iChannel0,             /* random digit SDF */  \
          (  vec2( c = int(10.*H(P)+ceil(P.y))%10, 12-c/16 )     \
            +( vec2(.25,.15) + vec2(.5,.7) * fract(P.xy) )       \
          ) /16. ) 

void mainImage(out vec4 O, vec2 U)
{
    float t=9.,r,a; int c;
    vec3  R = iResolution,
          D = normalize( vec3(U+U, -2.*R.y) - R ),              // ray direction
          p = vec3(0,0,10), q,P,                                // marching point along ray 
          C = iMouse.z > 0. ? 8.*iMouse.xyz/R -4.               // camera control
                          : 3.* cos(.3*iTime + vec3(0,11,0));   // demo mode
    for ( O=vec4(1); O.x > 0. && t > .01; O-=.01 )              // march scene
        q = p, 
        q.yz *= rot(-C.y),                                      // rotations
        q.xz *= rot(-C.x),
        r = length(q.yz) - 4.,a = atan(q.x,r),
        t =  abs( length(vec2(q.x,r)) - 1. ),                   // torus surface
        P.xy = vec2( 4.*atan(q.z,q.y) , a ) /.628,              // surface parameterization
        P.y = mod( P.y + 5.* sin( (.3+3.*H(P)) * iTime ), 10.), // columns rotation
        t = max(   t,      T.w - .5 ),                          // draw torus-digits intersection
     // t = max( a=t-.01,  T.w - .5 ),
        p += .5*t*D;                                            // step forward = dist to obj  

    O *= O*O*O*1.5;                                             // color scheme
 // O *= texture(iChannel0,P.xy/10.).x;
 // O *= T.x;
 // if (t==a) O*=vec4(.3,0,0,0);
}
