void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0,uv);
}

vec2 c = vec2(-0.5,0.2);  // modify here


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 1.5;
    if (iFrame < 1) {
        vec2 uv = fragCoord.xy / iResolution.xy;
        vec2 inside = 4.0*uv*(1.0-uv);
        fragColor = vec4(clamp(vec3(3.*inside.x*inside.y), 0.0, 1.0), 1);
        return;
    }
    vec2 z = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y * a;
    vec2 w = vec2(z.x*z.x-z.y*z.y,2.*z.x*z.y) + c;
    vec2 w_coord = (w / a * iResolution.y + iResolution.xy) / 2.0;
    vec2 w_uv = w_coord / iResolution.xy;
    fragColor = texture(iChannel0, w_uv);
}
