void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


     float cy  = 0.5-(sin(iTime)/3.0);
     float cx = 0.5 -(sin(sin(iTime))/8.0);
   ;
    float r3 = 13.0;
    float r2 = 50.0*iTime*iTime;
    float x = uv.x-cx;
    float y = uv.y-cy;
    float ci = x*x+y*y;

    if( (ci <= (r3*r3)*1.0005) && ci >=(r3*r2)*-1.0005)
    {
    float xa =(uv.x-cx)*(uv.x-cx);
    float yb = (uv.y-cy)*(uv.y-cy);
    float r = sqrt(xa+yb);
        fragColor = vec4(0.0,sin(iTime*r),cos(r*iTime),1.0);
        }

     else
         fragColor = vec4(0.0,0.0,0.0,1.0);


}
