#define T (iTime)
#define M_PI (3.1416)
#define TAU (M_PI * 2.0)


vec3 render_i(vec2 uv) {
    vec2 ouv = uv;
    int idx = -32;
    idx = (16*15)-idx;
    bool uppercase = false;
    uv.x += 9.;
    uv.y += (16.*16.0)-(uppercase ? 5.0 : 7.0);
    uv /= 16.0;

    uv.x /= 1.04;

    if (uv.x < 0.56 || uv.x >= 0.64) return vec3(0.0);
    float r = texture(iChannel0, uv).r;

    return vec3(r) * vec3(ouv.xy, 1.0);
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = fc/R.xy;

    uv.x *= (R.x / R.y);

    col = render_i(uv);

    O = vec4(col, 1.0);
}
