#define N 100

vec2 sq(vec2 p) {
    return vec2(
        p.x*p.x-p.y*p.y,
        2.*p.x*p.y
    );
}

// Taken from https://iquilezles.org/articles/palettes/
// and https://www.shadertoy.com/view/ll2GD3
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 mypal(in float t) {
    bool part1 = sin(.25*iTime) > 0.;
    return part1 ? 
        pal(t, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.30, 0.20, 0.20))
        :
        pal(t, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
        // pal(t, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.00, 0.10, 0.20));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 _uv = uv;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    vec2 pos = vec2(1,0);
    bool part1 = sin(.5*iTime) > 0.;
    vec2 x, c;
    if(part1) {
      if(iMouse.z>0.) {
        uv *= .1;
        uv += mouse.xy - pos;
      } else {
        uv -= pos;
      }
      x = vec2(0.,sin(.2*iTime));
      c = uv;
    } else {
      float t_off = 9.;
      x = uv;
      c = vec2(cos(.2*iTime+t_off),sin(.314159265*iTime+t_off));
      if(iMouse.z>0.) {
        c = mouse.xy;
      }
    }

    int i = 0;
    for(; i < N; i++) {
        x = sq(x) + c;
        if(length(x) > 2.) break;
    }

    float t = float(i)/float(N);
    vec3 col = mypal(.5+.5*sin(10.*t+2.*iTime));

    if(i==N)col = vec3(0);
    // if(length(_uv-mouse.xy) < .2) col = vec3(0,0,1.);
    // if(uv.y > .8) col = mypal(_uv.x);
    fragColor = vec4(col,1.0);
}
