vec2 sphIntersect(  vec2 ro,  vec2 rd,  vec2 ce, float ra )
{
    vec2 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    return vec2( -b-h,-b+h  );
}
