// Fork of "ping1" by makscee. https://shadertoy.com/view/DtscRH

#define PI     3.14159265

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xx;
    float offset = texture(iChannel0, uv * 200.5 + vec2(iTime * 0.5 + sin(iTime) * .2, 0)).x;
    uv *= 15.;
    uv += vec2(offset);

    float x = length(uv - round(uv)); 
    float dots = smoothstep(0., 0.5, x);

    vec3 col = vec3(
        dots * (0.5 + sin(iTime) / 2.),
        dots * (0.5 + sin(iTime + PI * 2. / 3.) / 2.),
        dots * (0.5 + sin(iTime + PI * 4. / 3.) / 2.)
    );

    fragColor = vec4(col,1.0);
}
