const vec3 cameraRotation = vec3(0.,0.,0.);
const float FOV = 60.;
const float maxDist = 1000.;

const vec3 sphereCenter = vec3(0.,0.,10.);
const float sphereRadius = 2.;

const vec3 sunDirection = normalize(vec3(1.,0.2,-0.3));

#define DEG2RAD 0.01745329251

float easeInOutQuint(float x) {
    return x < 0.5 ? 16. * x * x * x * x * x : 1. - pow(-2. * x + 2., 5.) / 2.;
}

float easeInCubic(float x) {
    return x * x * x;
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 rotateX(float angle, vec3 vector) {
    return vec3(
        vector[0] * cos(angle) - vector[1] * sin(angle),
        vector[0] * sin(angle) + vector[1] * cos(angle),
        vector[2]
    );
}

vec3 rotateY(float angle, vec3 vector) {
    return vec3(
        vector[0] * cos(angle) + vector[2] * sin(angle),
        vector[1],
        -vector[0] * sin(angle) + vector[2] * cos(angle)
    );
}

vec3 rotateZ(float angle, vec3 vector) {
    return vec3(
        vector[0],
        vector[1] * cos(angle) - vector[2] * sin(angle),
        vector[1] * sin(angle) + vector[2] * cos(angle)
    );
}

vec3 rotateVector(vec3 vector, vec3 rotation) {
    vector = rotateZ(rotation[0] * DEG2RAD, vector);
    vector = rotateY(rotation[1] * DEG2RAD, vector);
    vector = rotateX(rotation[2] * DEG2RAD, vector);
    return vector;
}

float lerp(float a, float b, float t) {
  return a + t * (b - a);
}

float sphereSDF(vec3 center, float radius, vec3 point) {
    return length(center - point) - radius;
}

float sdBox( vec3 center, vec3 corner, vec3 point)
{
  vec3 p = point - center;
  vec3 b = corner;
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

const vec3 FORWARD = vec3(0.,0.,1.);

const float outlineDist = 0.5;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Calculate vector direction
    float aspect = iResolution.x/iResolution.y;
    float verticalFOV = FOV * aspect;

    float bottomXrot = -verticalFOV/2.;
    float topXrot = verticalFOV/2.;
    float leftYrot = -FOV/2.;
    float rightYrot = FOV/2.;

    float Xrot = lerp(bottomXrot, topXrot, uv.x);
    float Yrot = lerp(leftYrot, rightYrot, uv.y);

    // Raycast

    vec2 mouseUV = iMouse.xy / iResolution.xy * 0.5;
    vec3 newRotation = cameraRotation + vec3(mouseUV.xy,0.) * 80.;

    vec3 cameraForward = rotateVector(FORWARD, newRotation);
    vec3 ray = rotateVector(cameraForward, vec3(Xrot, Yrot, 0.));

    float distTravelled = 0.;
    vec3 curPos = vec3(0,0,mouseUV.x * 20.);

    vec3 f = vec3(0.,0.,0.);
    float lowestCol = outlineDist;
    vec3 lowestPoint = vec3(0,0,0);
    float lowestStrength = 0.;

    vec3 curSunDirection = normalize(sunDirection + vec3(0.,cos(iTime), sin(iTime)));

    while (distTravelled < maxDist) {
        float radius = 3. + sin(iTime / 2.);
        float sdf = length(curPos - sphereCenter) - radius;
        // float sdf = sdBox(sphereCenter, vec3(1.,1.,1.) * radius, curPos);

        if (sdf < outlineDist && sdf < lowestCol) {
            vec3 normal = normalize(curPos - sphereCenter);
            lowestStrength = dot(normal, curSunDirection);
            lowestCol = sdf;
        }

        if (sdf < 0.02) {
            vec3 normal = normalize(curPos - sphereCenter);
            float sunStrength = dot(normal, curSunDirection);
            float highlight = easeInCubic(sunStrength);
            f = hsv2rgb(vec3(mod(sunStrength + 0.2, 1.), 0.3, highlight));
            break;
        }

        curPos += ray * sdf;
        distTravelled += sdf;
    }

    if (lowestCol >= outlineDist) {
        lowestCol = 0.;
    }

    lowestCol *= lowestStrength;

    // Export
    vec3 col = f;
    col += hsv2rgb(vec3((2. - lowestStrength) * 0.5, 1., lowestCol)) * lowestStrength;
    fragColor = vec4(col,1.0);
}
