// Fork of "Hot blob trails" by xavierseb. https://shadertoy.com/view/WlsGRM
// 2023-07-29 15:18:44

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = vec2(fragCoord.xy/iResolution.xy);

    vec4 flow = texture(iChannel0,uv);
    vec4 tex = texture(iChannel1, uv);
    vec4 mixTex = mix(tex, flow, 0.5);
    vec4 divideTex = flow * tex;

	fragColor = vec4(divideTex);
}

// basic feedback mechanism by Xavierseb
void mainImage( out vec4 fragColor, in vec2 fragCoord ){    

    vec2 mouse = iMouse.xy;
    // if mouse not detected do something
    if(mouse.x <= 0.) mouse = vec2( iResolution.x * (sin(iTime)+1.)/2., iResolution.y * (cos(iTime)+1.) /2.);

    // diameter of blob and intensity in same formula because why not
    vec3 blob = vec3(.11-clamp(length((fragCoord.xy-mouse.xy)/iResolution.x),0.,.11))*2.;

    vec3 stack= texture(iChannel0,fragCoord.xy/iResolution.xy).xyz * vec3(0.99,.982,.93);

    fragColor = vec4(stack + blob, 1.0);
}
