// THE SUN

vec3 paletteMix(float t, vec3 a, vec3 b, vec3 c, vec3 d) {
  return a + b*cos(6.28318*(c*t+d));
}

vec3 palette(in float t) {
    const vec3 a = vec3(0.178, 0.788, 1.178);
    const vec3 b = vec3(-0.699, 0.091, -0.699);
    const vec3 c = vec3(0.987, 1.150, 0.355);
    const vec3 d = vec3(0.998, 1.333, 1.718);
    return paletteMix(t, a, b, c, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;
    float a = (0.21+cos(iTime*1.7)*0.02) / length(uv);
    float b = step(0.38, length(uv));
    vec3 col = palette(a) * b + 1.0 - b;
    fragColor = vec4(col,1.0);
}
