float PI = 3.1415926536;

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}


vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, s, -s, c);
	return m * v;
}

float sdEquilateralTriangle( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

float zigzag(float t) {
    return mod(t,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 uv0 = uv;
    vec3 acc = vec3(0.0);

    for(float i=0.0; i<4.0; i++) {

        float d = length(uv*i) + exp(-length(uv0));


        vec3 mask = 0.5*pal(length(uv0)+0.4*iTime, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.25) );

        d = sin(8.0*d+cos(i*PI)*iTime)/8.0;

        if (i==0.0) {
            d = abs(sdEquilateralTriangle(rotate(uv,iTime),0.4));
            d += abs(sdEquilateralTriangle(rotate(uv,-iTime),0.4));
            mask=vec3(2.0);
        }
        d = abs(d);


        d = pow(0.01/d,1.2);


        acc += d * mask;
        uv = fract(uv*1.5)-0.5;

    }




    // Output to screen
    fragColor = vec4(acc, 0.0);
}
