mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 C, in vec2 FC )
{
    float o,c,i,e,f,s,g,k=.01,p2;
    vec2 r = iResolution.xy;
    float t = iTime;
    for(o++,c++;i++<1e2;g+=min(f,max(.03,e))*.4)
        {s=2.;vec3 p=vec3((FC.xy-r/s)/r.y*g,g-s),p1=p;
        p.yz*=rotate2D(-.8);
        p.z+=t;
        for(e=f=p.y;s<2e2;s/=.4)
            p1 = sin(p.yzx*s/2.)/s/2.,
            p2 = abs(sin(p1.x+p1.y+p1.z)),
            e += p2,
            f += p2*1.5;
    o+=(f>k*k?e:-exp(-f*f))*o*k;}
    c+=e;

    C = vec4(o);
    C.rg -= c*.04;

}

float fmod(float a, float b){
    return
        mod(floor(mod(a,1.+a/2.)),b)
        //mod(floor(mod(a*floor(a/b),1.+a/2.)),b)
        //mod(floor(mod(a,1.+floor(a/2.)+floor(a/4.)+floor(a/8.))),b)
    ;
}

vec2 mainSound(int samp, float time){
  time =
      (time + mod(time,1.+floor(time)))/2.
      //mod(time+floor(time/8.),1.+floor(time))+floor(time*8.);
  ;
  float s1 = 8.,
  t=time/(1.+fmod(time,2.)),
  m3 =
      fmod(floor(t*s1+fmod(t,s1)),s1)
      //fmod(floor(t*pow(s1,fmod(t,2.))+fmod(t,s1)),s1)
  ;
  t *= s1*s1/(1.+fmod(t/s1,2.));
  float a = 64.*((1.-sqrt(max(fract(t/s1/s1/2.),fract(t/s1/2.)))))*.2,
  nb = pow(2.,(m3+1. + fmod(t/s1,s1))/5.+6.5);
  return abs(.5-vec2(fract(time*nb*.998),fract(time*nb)))*a;
}
