void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r= (sin(iTime-uv.y*5.0)+2.0)/4.0;

    if(r> uv.x){
        fragColor = vec4(0.2,0.2,1,0)*(r+0.5);
        }
    else if( r<= uv.x/2.0){
        fragColor = vec4(1,0,0.,0)*(r+0.5);
        }
    else{
        fragColor = vec4(1,1,1,0)*(r+1.0);
    }

}
